		<link rel="stylesheet" href="./Paiement_securise_files/standard.css">


</head>
<body>
	<div id="content-checkout">
		<div class="page checkout unbordered-checkout fill">
			<div class="container_wrapper" style="background-color: rgb(223, 223, 223);">
				<div class="container container-standard">
					<div class="sidebar">
						<div class="container-transaction">
							<h4>Votre transaction</h4>
							<span class="icon icon-amount"></span>
							<div class="info-transaction">
								
								<div class="txt-transaction">
									<label class="label-info">Montant</label>
									 <label class="label-value" id="product-price-show">10,00&nbsp;€</label>
								</div>
							</div>


							<span class="icon icon-transaction"></span>
							<div class="info-transaction">
								<div class="txt-transaction">
									<label class="label-info" >N° de transaction</label>
									<label class="label-value">1234567</label>
								</div>
							</div>
						</div>
						<div class="separator"></div>

						</div>


					<div class="panel panel-default" id="panel-paiement-box">
							<div class="panel-body no-padding">
								<section class="form-section form-card-section" id="cardBox">

									<h3 class="form-section-label" id="cardOwnerBoxTitle">Propriétaire de la carte</h3>
									<div class="form-control">
										<input type="TEXT" Maxlength="23" id="owner" class="mBottom15" autocomplete="off" placeholder="Propriétaire" required="required">
										<img src="./Paiement_securise_files/icon-badge.jpG" id="input_img">
									</div>

								<h3 class="form-section-label" id="cardBoxTitle">Votre carte</h3>

								<div class="card-container">
									<img src="./Paiement_securise_files/visa.png" class="not-applicable" id="visa">
									<img src="./Paiement_securise_files/mastercard.png" class="not-applicable" alt="Les cartes MasterCard sont autorisées" id="mastercard">
								</div>

								<div class="form-control" id="card-number-field">
									<input type="tel" data-payplug="card_number" maxlength="23" id="paymentCardNumber" class="mBottom15" autocomplete="off" placeholder="Numéro de carte" required="required" pattern="^((\d{4}\s{0,2}){3}\d{4}(\s?\d{0,3}))$" pattern-function="luhn">
									<img src="./Paiement_securise_files/icon-numero-carte.jpG" id="input_img">
								</div>

								<div class="input-row">
									<div class="form-control" style="flex-grow: 1.2"  >
										<span class="form-section-label" >Date d'expiration</span>
										<span class="form-section-label"><br/></span>
										<select >
										<option value="mois" selected disabled>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Mois</option>
										<option value="mois01">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;01</option>
										<option value="mois02">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;02</option>
										<option value="mois03">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;03</option>
										<option value="mois04">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;04</option>
										<option value="mois05">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;05</option>
										<option value="mois06">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;06</option>
										<option value="mois07">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;07</option>
										<option value="mois08">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;08</option>
										<option value="mois09">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;09</option>
										<option value="mois10">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10</option>
										<option value="mois11">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;11</option>
										<option value="mois12">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;12</option>
										</select>
										<img src="./Paiement_securise_files/icon-calendar.PNG" id="input_img">
									</div>

									<div class="form-control" style="flex-grow: 1.2">
										<span class="form-section-label" ><br/></span>
										<select>
										<option value="annee" selected disabled>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Année</option>
										<option value="annee2017">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;17</option>
										<option value="annee2018">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;18</option>
										<option value="annee2019">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;19</option>
										<option value="annee2020">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20</option>
										<option value="annee2021">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;21</option>
										<option value="annee2022">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;22</option>
										</select>
										<img src="./Paiement_securise_files/icon-calendar.png" id="input_img">
									</div>


								</div>

								<div class="form-control">
										<span class="form-section-label">CRYPTOGRAMME</span>
										<img src="./Paiement_securise_files/icon-cadenas.PNG" id="input_img">
										<input type="tel" data-payplug="card_cvv" maxlength="3" size="5" id="paymentCardCvv" autocomplete="off" placeholder="123" required="required" pattern="^\d{3}$">
								</div>



								<div class="icon-btn">
								
								<input type="submit" name="paymentInfoFormSubmit" id="payButton" value="PAYER 10,00 €" class="button button-full button-submit custom_pay_color" style="background-color: rgb(32, 33, 28); color: white;">
							</div>

							</section>
							</div>

							<div class="separator separator-footer">
							</div>
							<div class="panel-footer panel-logo-footer">
								<img class="logo-verif" src="./Paiement_securise_files/logo_secure_default2.png">

							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
	</div>
</body>
<script src="vendor/jquery/jquery.min.js"></script>
<script src="./Paiement_securise_files/js/jquery.payform.min.js"></script>
<script src="./Paiement_securise_files/js/Checkinput.js"></script>

</html>
